import requests

endpoint = "https://graph.realtor.com/graphql"

introspection_query = """
{
  __schema {
    types {
      name
      fields {
        name
        type {
          name
          kind
          ofType {
            name
            kind
          }
        }
      }
    }
  }
}
"""

response = requests.post(endpoint, json={'query': introspection_query})
schema = response.json()

# Save or print the schema for further exploration
print(schema)
